import {useNavigation } from '@react-navigation/core'
import React from 'react'
import  {useState} from 'react'
import { SafeAreaView, StyleSheet, TextInput, Text, TouchableOpacity, View, TouchableHighlightComponent } from "react-native";
import { theme } from '../config/colors'
// import { auth } from '../../firebase-config'

const Login = ()=>{
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')

    const navigation = useNavigation()

    const handleLogin = () =>{
        auth.signIn(email,password).then(userCredentials =>{
            const user = userCredentials.user;
            console.info('Logged in with: ',user.email);
            navigation.navigate("main");
        })
        .catch(error => console.error(error.message))
    }

    return(
        <SafeAreaView style={styles.container}>
            <Text style={styles.logIn}>Log in</Text>
            <TextInput
            placeholder='Email'
            value={email}
            onChangeText={text => setEmail(text)}
            style={styles.input}
            />
            <TextInput
            placeholder='Password'
            value={password}
            onChangeText={text => setPassword(text)}
            style={styles.input}
            secureTextEntry
            />
            <View style={styles.forgotPassword}>
                <TouchableOpacity onPress={()=> navigation.navigate('resetPassword',{name:'resetPassword'})}>
                <Text style={styles.forgot}>Forgot your password?</Text>
                </TouchableOpacity>
            </View>
            <TouchableOpacity onPress={handleLogin} style={styles.button}>
                <Text style={styles.buttonText}>Log in</Text>
            </TouchableOpacity>
            <View style={styles.row}>
                <Text style={styles.dont}>Don’t have an account? </Text>
                <TouchableOpacity onPress={() => navigation.navigate('Register', { name: 'Register' })}>
                    <Text style={styles.link}>Sign up</Text>
                </TouchableOpacity>
            </View>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container:{
        flex:1,
        paddingTop:"20%",
        backgroundColor:"#050517",
        alignItems:'center'
    },
    logIn:{
        color:'white',
        fontSize:44,
        marginTop:50,
        marginBottom:50
    },
    input:{
        width:'90%',
        height:40,
        marginTop:25,
        borderWidth:1.5,
        padding:10,
        borderTop: 'none',
        borderColor:theme.colors.colorGrey,
    },
    forgotPassword:{
        width:'90%',
        marginBottom:24,
        alignItems:'flex-end'
    },
    row:{
        flexDirection:'row',
        marginTop:20,
        justifyContent:'center'
    },
    forgot:{
        fontSize:15,
        color:theme.colors.colorPink,
        marginTop:20
    },
    link:{
        fontWeight:'bold',
        color:theme.colors.colorPink
    },
    button:{
        alignItems:'center',
        backgroundColor:theme.colors.colorPink,
        padding:10,
        width:'80%'
    },
    buttonText:{
        fontSize:18,
        fontWeight:'700',
        color:theme.colors.lightText,
    },
    dont:{
        color:'white'
    }
})

export default Login;
