import {useNavigation } from '@react-navigation/core'
import React from 'react'
import  {useState} from 'react'
import { SafeAreaView, StyleSheet, TextInput, Text, TouchableOpacity, View } from "react-native";
// import { auth } from '../../firebase-config'

const Login = ()=>{
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')

    const navigation = useNavigation()

    const handleLogin = () =>{
        auth.signIn(email,password).then(userCredentials =>{
            const user = userCredentials.user;
            console.info('Logged in with: ',user.email);
            navigation.navigate("main");
        })
        .catch(error => console.error(error.message))
    }

    return(
        <SafeAreaView style={styles.container}>
            <Text>Log in</Text>
            <TextInput
            placeholder='Email'
            value={email}
            onChangeText={text => setEmail(text)}
            style={styles.input}
            />
            <TextInput
            placeholder='Password'
            value={password}
            onChangeText={text => setPassword(text)}
            style={styles.input}
            secureTextEntry
            />
            <View style={styles.forgotPassword}>
                <TouchableOpacity onPress={()=> navigation.navigate('resetPassword',{name:'resetPassword'})}>
                <Text style={styles.forgot}>Forgot your password?</Text>
                </TouchableOpacity>
            </View>
            <TouchableOpacity onPress={handleLogin} style={styles.button}>
                <Text style={styles.buttonText}>Log in</Text>
            </TouchableOpacity>
            <View style={styles.row}>
                <Text>Donâ€™t have an account? </Text>
                <TouchableOpacity onPress={() => navigation.navigate('Register', { name: 'Register' })}>
                    <Text style={styles.link}>Sign up</Text>
                </TouchableOpacity>
            </View>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container:{
        flex:1,
        paddingTop:"20%",
        backgroundColor:"black"
    },
    input:{
        height:40,
        margin:15,
        borderWidth:1,
        padding:10
    },
    forgotPassword:{
        width:'100%',
        alignItems:'flex-end',
        marginBottom:24
    },
    row:{
        flexDirection:'row',
        marginTop:20,
        justifyContent:'center'
    }
})

export default Login;
