import {useNavigation } from '@react-navigation/core'
import React from 'react'
import  {useState} from 'react'
import { SafeAreaView, StyleSheet, TextInput, Text, TouchableOpacity, View } from "react-native";
// import { auth } from '../../firebase-config'

const Login = ()=>{
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')

    const navigation = useNavigation()

    const handleLogin = () =>{
        auth.signIn(email,password).then(userCredentials =>{
            const user = userCredentials.user;
            console.info('Logged in with: ',user.email);
            navigation.navigate("main");
        })
        .catch(error => console.error(error.message))
    }

    return(
        <SafeAreaView style={styles.container}>
            <Text>Log in</Text>
            <TextInput
            placeholder='Email'
            value={email}
            onChangeText={text => setEmail(text)}
            style={styles.input}
            />
            <TextInput
            placeholder='Password'
            value={password}
            onChangeText={text => setPassword(text)}
            style={styles.input}
            secureTextEntry
            />
            <View style={styles.forgotPassword}>
                <TouchableOpacity onPress={()=> navigation.navigate('resetPassword',{name:'resetPassword'})}>
                    
                </TouchableOpacity>
            </View>
        </SafeAreaView>
    )
};

export default Login;
